// FILE: store.ts
import { create } from 'zustand';

interface Book {
  id: string;
  title: string;
  author: string;
}

interface Borrowing {
  id: string;
  bookId: string;
  userId: string;
  borrowedDate: string;
  returnDate?: string;
}

interface User {
  id: string;
  name: string;
  email: string;
  token: string;
}

interface AppState {
  books: Book[];
  borrowings: Borrowing[];
  user: User | null;
  addBook: (book: Book) => void;
  removeBook: (bookId: string) => void;
  addBorrowing: (borrowing: Borrowing) => void;
  returnBorrowing: (borrowingId: string) => void;
  setUser: (user: User) => void;
  clearUser: () => void;
}

const useStore = create<AppState>((set) => ({
  books: [],
  borrowings: [],
  user: null,
  addBook: (book) =>
    set((state) => ({
      books: [...state.books, book],
    })),
  removeBook: (bookId) =>
    set((state) => ({
      books: state.books.filter((book) => book.id !== bookId),
    })),
  addBorrowing: (borrowing) =>
    set((state) => ({
      borrowings: [...state.borrowings, borrowing],
    })),
  returnBorrowing: (borrowingId) =>
    set((state) => ({
      borrowings: state.borrowings.map((borrowing) =>
        borrowing.id === borrowingId ? { ...borrowing, returnDate: new Date().toISOString() } : borrowing
      ),
    })),
  setUser: (user) =>
    set(() => ({
      user,
    })),
  clearUser: () =>
    set(() => ({
      user: null,
    })),
}));

export default useStore;
