// src/components/BooksList.tsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import useStore from '../store/store';

const BooksList: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newBook, setNewBook] = useState({
    title: '',
    author: '',
    genre: '',
    isbn: '',
    total_copies: 1,
  });
  const [error, setError] = useState('');

  const { books, user, isLoading, setBooks, addBook, removeBook, setLoading, clearUser } = useStore();
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }
    fetchBooks();
  }, [user, navigate]);

  const fetchBooks = async (search?: string) => {
    if (!user) return;

    setLoading(true);
    setError('');

    try {
      const url = search 
        ? `http://localhost:8000/api/books?search=${encodeURIComponent(search)}`
        : 'http://localhost:8000/api/books';

      const response = await fetch(url, {
        headers: {
          'Authorization': `Bearer ${user.token}`,
          'Accept': 'application/json',
        },
      });

      if (!response.ok) {
        if (response.status === 401) {
          clearUser();
          navigate('/login');
          return;
        }
        throw new Error('Failed to fetch books');
      }

      const data = await response.json();
      setBooks(data.data || data);
    } catch (error) {
      console.error('Error fetching books:', error);
      setError('Failed to load books');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    fetchBooks(searchTerm);
  };

  const handleCreateBook = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!user || user.role !== 'librarian') return;

    setLoading(true);
    setError('');

    try {
      const response = await fetch('http://localhost:8000/api/books', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${user.token}`,
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
        body: JSON.stringify(newBook),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to create book');
      }

      const data = await response.json();
      addBook(data.data || data);
      setNewBook({ title: '', author: '', genre: '', isbn: '', total_copies: 1 });
      setShowCreateForm(false);
    } catch (error) {
      console.error('Error creating book:', error);
      setError(error instanceof Error ? error.message : 'Failed to create book');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteBook = async (bookId: number) => {
    if (!user || user.role !== 'librarian') return;
    if (!window.confirm('Are you sure you want to delete this book?')) return;

    setLoading(true);
    setError('');

    try {
      const response = await fetch(`http://localhost:8000/api/books/${bookId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${user.token}`,
          'Accept': 'application/json',
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to delete book');
      }

      removeBook(bookId);
    } catch (error) {
      console.error('Error deleting book:', error);
      setError(error instanceof Error ? error.message : 'Failed to delete book');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    clearUser();
    navigate('/login');
  };

  if (!user) return null;

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      {/* Header */}
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        marginBottom: '30px',
        paddingBottom: '20px',
        borderBottom: '1px solid #eee'
      }}>
        <div>
          <h1>Library Management System</h1>
          <p>Welcome, {user.name} ({user.role})</p>
        </div>
        <button 
          onClick={handleLogout}
          style={{ 
            padding: '8px 16px', 
            backgroundColor: '#dc3545', 
            color: 'white', 
            border: 'none', 
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Logout
        </button>
      </div>

      {/* Error Display */}
      {error && (
        <div style={{ 
          color: 'red', 
          backgroundColor: '#ffebee', 
          padding: '10px', 
          borderRadius: '4px', 
          marginBottom: '20px' 
        }}>
          {error}
        </div>
      )}

      {/* Search and Create Section */}
      <div style={{ marginBottom: '30px' }}>
        <div style={{ display: 'flex', gap: '20px', alignItems: 'flex-end', marginBottom: '20px' }}>
          {/* Search Form */}
          <form onSubmit={handleSearch} style={{ display: 'flex', gap: '10px', flex: 1 }}>
            <div style={{ flex: 1 }}>
              <label style={{ display: 'block', marginBottom: '5px' }}>Search Books:</label>
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search by title, author, or genre..."
                style={{ 
                  width: '100%', 
                  padding: '8px', 
                  border: '1px solid #ccc', 
                  borderRadius: '4px' 
                }}
              />
            </div>
            <button 
              type="submit" 
              disabled={isLoading}
              style={{ 
                padding: '8px 16px', 
                backgroundColor: '#007bff', 
                color: 'white', 
                border: 'none', 
                borderRadius: '4px',
                cursor: isLoading ? 'not-allowed' : 'pointer'
              }}
            >
              Search
            </button>
            <button 
              type="button" 
              onClick={() => {
                setSearchTerm('');
                fetchBooks();
              }}
              disabled={isLoading}
              style={{ 
                padding: '8px 16px', 
                backgroundColor: '#6c757d', 
                color: 'white', 
                border: 'none', 
                borderRadius: '4px',
                cursor: isLoading ? 'not-allowed' : 'pointer'
              }}
            >
              Clear
            </button>
          </form>

          {/* Create Book Button (Librarian Only) */}
          {user.role === 'librarian' && (
            <button 
              onClick={() => setShowCreateForm(!showCreateForm)}
              style={{ 
                padding: '8px 16px', 
                backgroundColor: '#28a745', 
                color: 'white', 
                border: 'none', 
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              {showCreateForm ? 'Cancel' : 'Add New Book'}
            </button>
          )}
        </div>

        {/* Create Book Form */}
        {showCreateForm && user.role === 'librarian' && (
          <form 
            onSubmit={handleCreateBook}
            style={{ 
              backgroundColor: '#f8f9fa', 
              padding: '20px', 
              borderRadius: '4px', 
              border: '1px solid #dee2e6' 
            }}
          >
            <h3>Add New Book</h3>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px', marginBottom: '15px' }}>
              <div>
                <label style={{ display: 'block', marginBottom: '5px' }}>Title:</label>
                <input
                  type="text"
                  value={newBook.title}
                  onChange={(e) => setNewBook({...newBook, title: e.target.value})}
                  required
                  style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }}
                />
              </div>
              <div>
                <label style={{ display: 'block', marginBottom: '5px' }}>Author:</label>
                <input
                  type="text"
                  value={newBook.author}
                  onChange={(e) => setNewBook({...newBook, author: e.target.value})}
                  required
                  style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }}
                />
              </div>
              <div>
                <label style={{ display: 'block', marginBottom: '5px' }}>Genre:</label>
                <input
                  type="text"
                  value={newBook.genre}
                  onChange={(e) => setNewBook({...newBook, genre: e.target.value})}
                  required
                  style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }}
                />
              </div>
              <div>
                <label style={{ display: 'block', marginBottom: '5px' }}>ISBN:</label>
                <input
                  type="text"
                  value={newBook.isbn}
                  onChange={(e) => setNewBook({...newBook, isbn: e.target.value})}
                  required
                  style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }}
                />
              </div>
              <div>
                <label style={{ display: 'block', marginBottom: '5px' }}>Total Copies:</label>
                <input
                  type="number"
                  min="1"
                  value={newBook.total_copies}
                  onChange={(e) => setNewBook({...newBook, total_copies: parseInt(e.target.value)})}
                  required
                  style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }}
                />
              </div>
            </div>
            <button 
              type="submit" 
              disabled={isLoading}
              style={{ 
                padding: '10px 20px', 
                backgroundColor: '#28a745', 
                color: 'white', 
                border: 'none', 
                borderRadius: '4px',
                cursor: isLoading ? 'not-allowed' : 'pointer'
              }}
            >
              {isLoading ? 'Creating...' : 'Create Book'}
            </button>
          </form>
        )}
      </div>

      {/* Loading State */}
      {isLoading && (
        <div style={{ textAlign: 'center', padding: '20px' }}>
          Loading books...
        </div>
      )}

      {/* Books List */}
      {!isLoading && (
        <div>
          <h2>Books ({books.length})</h2>
          {books.length === 0 ? (
            <p>No books found.</p>
          ) : (
            <div style={{ display: 'grid', gap: '15px' }}>
              {books.map((book) => (
                <div 
                  key={book.id}
                  style={{ 
                    border: '1px solid #ddd', 
                    borderRadius: '4px', 
                    padding: '15px',
                    backgroundColor: 'white'
                  }}
                >
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                    <div style={{ flex: 1 }}>
                      <h3 style={{ margin: '0 0 10px 0', color: '#333' }}>{book.title}</h3>
                      <p style={{ margin: '5px 0', color: '#666' }}>
                        <strong>Author:</strong> {book.author}
                      </p>
                      <p style={{ margin: '5px 0', color: '#666' }}>
                        <strong>Genre:</strong> {book.genre}
                      </p>
                      <p style={{ margin: '5px 0', color: '#666' }}>
                        <strong>ISBN:</strong> {book.isbn}
                      </p>
                      <p style={{ margin: '5px 0', color: '#666' }}>
                        <strong>Copies:</strong> {book.available_copies || 0} / {book.total_copies} available
                      </p>
                    </div>
                    <div style={{ display: 'flex', gap: '10px' }}>
                      <button
                        onClick={() => navigate(`/books/${book.id}`)}
                        style={{ 
                          padding: '6px 12px', 
                          backgroundColor: '#007bff', 
                          color: 'white', 
                          border: 'none', 
                          borderRadius: '4px',
                          cursor: 'pointer'
                        }}
                      >
                        View Details
                      </button>
                      {user.role === 'librarian' && (
                        <>
                          <button
                            onClick={() => navigate(`/books/${book.id}/edit`)}
                            style={{ 
                              padding: '6px 12px', 
                              backgroundColor: '#ffc107', 
                              color: 'black', 
                              border: 'none', 
                              borderRadius: '4px',
                              cursor: 'pointer'
                            }}
                          >
                            Edit
                          </button>
                          <button
                            onClick={() => handleDeleteBook(book.id)}
                            disabled={isLoading}
                            style={{ 
                              padding: '6px 12px', 
                              backgroundColor: '#dc3545', 
                              color: 'white', 
                              border: 'none', 
                              borderRadius: '4px',
                              cursor: isLoading ? 'not-allowed' : 'pointer'
                            }}
                          >
                            Delete
                          </button>
                        </>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default BooksList;
